import React from 'react';
import { Form, Input } from 'antd';

const CompanyDetails = ({ form, onChange }) => {
    const { getFieldDecorator } = form;

    const handleValueChange = (field, value) => {
        onChange(field, value);
    };

    return (
        <Form>
            <Form.Item label="Client Company">
                {getFieldDecorator('clientCompany', {
                    rules: [{ required: true, message: 'Please enter client company' }],
                })(<Input placeholder="Enter client company" onChange={(e) => handleValueChange('clientCompany', e.target.value)} />)}
            </Form.Item>
            <Form.Item label="Service Company">
                {getFieldDecorator('serviceCompany', {
                    rules: [{ required: true, message: 'Please enter service company' }],
                })(<Input placeholder="Enter service company" onChange={(e) => handleValueChange('serviceCompany', e.target.value)} />)}
            </Form.Item>
        </Form>
    );
};

export default Form.create({ name: 'company_details' })(CompanyDetails);
