import React from 'react';
import { Form, Input, DatePicker, Select } from 'antd';

const { Option } = Select;

const ProjectDetails = ({ form, onChange }) => {
    const { getFieldDecorator } = form;

    const handleValueChange = (field, value) => {
        onChange(field, value);
    };

    return (
        <Form>
            <Form.Item label="Project Name">
                {getFieldDecorator('projectName', {
                    rules: [{ required: true, message: 'Please enter project name' }],
                })(<Input placeholder="Enter project name" onChange={(e) => handleValueChange('projectName', e.target.value)} />)}
            </Form.Item>
            <Form.Item label="Start Date">
                {getFieldDecorator('startDate', {
                    rules: [{ required: true, message: 'Please select start date' }],
                })(<DatePicker onChange={(date, dateString) => handleValueChange('startDate', dateString)} />)}
            </Form.Item>
            <Form.Item label="End Date">
                {getFieldDecorator('endDate', {
                    rules: [{ required: true, message: 'Please select end date' }],
                })(<DatePicker onChange={(date, dateString) => handleValueChange('endDate', dateString)} />)}
            </Form.Item>
            <Form.Item label="Priority">
                {getFieldDecorator('priority', {
                    rules: [{ required: true, message: 'Please select priority' }],
                })(
                    <Select placeholder="Select priority" onChange={(value) => handleValueChange('priority', value)}>
                        <Option value="high">High</Option>
                        <Option value="medium">Medium</Option>
                        <Option value="low">Low</Option>
                    </Select>
                )}
            </Form.Item>
        </Form>
    );
};

export default Form.create({ name: 'project_details' })(ProjectDetails);
