import React from 'react';
import { Form, Select } from 'antd';

const { Option } = Select;

const EmployeeSelector = ({ form, employees, onChange }) => {
    const { getFieldDecorator } = form;

    const handleValueChange = (field, value) => {
        onChange(field, value);
    };

    return (
        <Form>
            <Form.Item label="Project Manager">
                {getFieldDecorator('projectManager', {
                    rules: [{ required: true, message: 'Please select project manager' }],
                })(
                    <Select
                        placeholder="Select project manager"
                        onChange={(value) => handleValueChange('projectManager', value)}
                    >
                        {employees.map((employee) => (
                            <Option key={employee.id} value={employee.id}>
                                {employee.name}
                            </Option>
                        ))}
                    </Select>
                )}
            </Form.Item>
            <Form.Item label="Select Employees">
                {getFieldDecorator('selectedEmployees', {
                    rules: [{ required: true, message: 'Please select employees' }],
                })(
                    <Select
                        mode="multiple"
                        placeholder="Select employees"
                        onChange={(value) => handleValueChange('selectedEmployees', value)}
                    >
                        {employees.map((employee) => (
                            <Option key={employee.id} value={employee.id}>
                                {employee.name}
                            </Option>
                        ))}
                    </Select>
                )}
            </Form.Item>
        </Form>
    );
};

export default Form.create({ name: 'employee_selector' })(EmployeeSelector);
