import React, { useState } from 'react';
import { Steps, Button, message } from 'antd';
import ProjectDetails from './ProjectDetails';
import CompanyDetails from './CompanyDetails';
import EmployeeSelector from './EmployeeSelector';
import FileUploader from './FileUploader';

const { Step } = Steps;

const ProjectWizard = () => {
    const [currentStep, setCurrentStep] = useState(0);
    const [formData, setFormData] = useState({});
    const steps = [
        {
            title: 'Project Details',
            content: <ProjectDetails onChange={handleFormChange} />,
        },
        {
            title: 'Company Details',
            content: <CompanyDetails onChange={handleFormChange} />,
        },
        {
            title: 'Employee Selector',
            content: <EmployeeSelector onChange={handleFormChange} employees={[]} />,
        },
        {
            title: 'File Uploader',
            content: <FileUploader onChange={handleFileUpload} />,
        },
    ];

    const handleFormChange = (field, value) => {
        setFormData({ ...formData, [field]: value });
    };

    const handleFileUpload = (file) => {
        setFormData({ ...formData, file });
    };

    const nextStep = () => {
        setCurrentStep(currentStep + 1);
    };

    const prevStep = () => {
        setCurrentStep(currentStep - 1);
    };

    const handleSubmit = () => {
        // Handle form submission here
        console.log(formData);
        message.success('Form submitted successfully!');
    };

    return (
        <div style={{ padding: '30px' }}>
            <Steps current={currentStep}>
                {steps.map((item) => (
                    <Step key={item.title} title={item.title} />
                ))}
            </Steps>
            <div style={{ marginTop: '30px' }}>{steps[currentStep].content}</div>
            <div style={{ marginTop: '30px' }}>
                {currentStep > 0 && (
                    <Button style={{ marginRight: '8px' }} onClick={prevStep}>
                        Previous
                    </Button>
                )}
                {currentStep < steps.length - 1 && (
                    <Button type="primary" onClick={nextStep}>
                        Next
                    </Button>
                )}
                {currentStep === steps.length - 1 && (
                    <Button type="primary" onClick={handleSubmit}>
                        Submit
                    </Button>
                )}
            </div>
        </div>
    );
};

export default ProjectWizard;
