import { useState, useEffect } from "react";
import { Link } from "react-router-dom";


export default function ProjectList() {
    const [projects, setProjects] = useState([]);

    useEffect(() => {
        getProjects();
    }, []);

    function getProjects() {
        fetch("http://localhost:4000/api/Project/allProjects")
            .then(response => {
                if (response.ok) {
                    return response.json()
                }
                throw new Error()
            })
            .then(data => {
                console.log(data)
                setProjects(data)
            })
            .catch(error => {
                alert("Unable to get the data")
            })
    }

    return (
        <div className="container my-4">
            <h2 className="text-center mb-4">
                Projects
            </h2>

            <div className="row mb-3">
                <div className="col">
                    <Link className="btn btn-primary me-1" to="/create" role="button"> Create Project </Link>
                    <button type="button" className="btn btn-outline-primary" onClick={getProjects}>Refresh</button>
                </div>

                <div className="col"></div>
            </div>

            <table className="table">
                <thead>
                    <tr>
                        <th>Project Name</th>
                        <th>Customer Company</th>
                        <th>Executor Company</th>
                        <th>Priority</th>
                        <th>Start Date</th>
                        <th>End Date</th>
                        <th>Project Manager</th>
                        <th>Actions</th>

                    </tr>
                </thead>
                <tbody>

                    {projects.map((project, index) => (
                        <tr key={index}>
                            <td>{project.name}</td>
                            <td>{project.customerCompany}</td>
                            <td>{project.executorCompany}</td>
                            <td>{project.priority}</td>
                            <td>{project.startDate}</td>
                            <td>{project.endDate}</td>
                            <td>{project.projectManager.firstName}</td>
                            <td>
                                {/* Action buttons */}
                                <a className="btn btn-primary me-1" to="projects/create" role="button"> Edit </a>
                                <button type="button" className="btn btn-outline-primary btn-danger text-white">Delete</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    )
}
