import React from 'react';

const EmployeeSelector = ({ employees, onChange }) => {
    return (
        <div className="card mb-3">
            <div className="card-body">
                <h5 className="card-title">Step 3: Select Employees</h5>
                <form>
                    <div className="form-group">
                        <label htmlFor="projectManager">Project Manager</label>
                        <select className="form-control" id="projectManager" onChange={(e) => onChange('projectManager', e.target.value)}>
                            {employees.map((employee) => (
                                <option key={employee.id} value={employee.id}>{employee.name}</option>
                            ))}
                        </select>
                    </div>
                    {/* Additional employee selection if needed */}
                </form>
            </div>
        </div>
    );
};

export default EmployeeSelector;
