import { useState, useEffect } from "react";
import { Link } from "react-router-dom";


export default function EmployeeList() {
    const [employees, setEmployees] = useState([]);

    useEffect(() => {
        getemployees();
    }, []);
    function handleDelete(employeeId) {
        fetch(`http://localhost:4000/api/Employee/deleteEmployee/${employeeId}`, {
            method: "DELETE"
        })
            .then(response => {
                if (response.ok) {
                    // Refresh the employee list after deletion
                    setEmployees(prevEmployees => prevEmployees.filter(emp => emp.employeeId !== employeeId));

                } else {
                    throw new Error();
                }
            })
            .catch(error => {
                alert("Unable to delete the employee");
            });
    }
    function getemployees() {
        fetch("http://localhost:4000/api/Employee/allEmployee")
            .then(response => {
                if (response.ok) {
                    return response.json()
                }
                throw new Error()
            })
            .then(data => {
                // console.log(data)
                setEmployees(data)
            })
            .catch(error => {
                alert("Unable to get the data")
            })
    }

    return (
        <div className="container my-4">
            <h2 className="text-center mb-4">
                Employees
            </h2>

            <div className="row mb-3">
                <div className="col">
                    <Link className="btn btn-primary me-1" to="employees/create" role="button"> Create Project </Link>
                    <button type="button" className="btn btn-outline-primary" onClick={getemployees}>Refresh</button>
                </div>

                <div className="col"></div>
            </div>

            <table className="table">
                <thead>
                    <tr>
                        <th>First Name</th>
                        <th>Last Name</th>
                        <th>Middle Name</th>
                        <th>Email</th>
                        <th>Position</th>
                        <th>Actions</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        employees.map((employees, index) => {

                            return (
                                <tr key={index}>
                                    <td>{employees.firstName}</td>
                                    <td>{employees.lastName}</td>
                                    <td>{employees.middleName}</td>
                                    <td>{employees.email}</td>
                                    <td>{employees.position}</td>


                                    <td style={{ width: "10px", whiteSpace: "nowrap" }}>
                                        <Link className="btn btn-primary me-1" to="/create" role="button"> Edit </Link>
                                        <button type="button" className="btn btn-outline-primary btn-danger text-white" onClick={() => handleDelete(employees.id)}>Delete</button>

                                    </td>
                                </tr>
                            )
                        })
                    }
                </tbody>
            </table>
        </div>
    )
}
