import React, { useState } from 'react';
import ProjectDetails from './ProjectDetails';
import CompanyDetails from './CompanyDetails';
import EmployeeSelector from './EmployeeSelector';
import FileUploader from './FileUploader';

const ProjectForm = () => {
    const [step, setStep] = useState(1); // Current step of the wizard
    const [formData, setFormData] = useState({}); // Data collected from form steps

    const handleStepChange = (field, value) => {
        setFormData({ ...formData, [field]: value });
    };

    const handleNextStep = () => {
        setStep(step + 1);
    };

    const handlePreviousStep = () => {
        setStep(step - 1);
    };

    const handleFormSubmit = (e) => {
        e.preventDefault();
        // Send formData to backend for processing
        console.log(formData);
    };

    const renderStep = () => {
        switch (step) {
            case 1:
                return <ProjectDetails onChange={handleStepChange} />;
            case 2:
                return <CompanyDetails onChange={handleStepChange} />;
            case 3:
                return <EmployeeSelector employees={[]} onChange={handleStepChange} />;
            case 4:
                return <FileUploader onFileChange={(file) => handleStepChange('file', file)} />;
            default:
                return null;
        }
    };

    return (
        <div className="container">
            <div className="row justify-content-center mt-5">
                <div className="col-lg-8">
                    <form onSubmit={handleFormSubmit}>
                        {renderStep()}
                        <div className="btn-group" role="group" aria-label="Wizard navigation">
                            {step > 1 && (
                                <button type="button" className="btn btn-secondary" onClick={handlePreviousStep}>
                                    Previous
                                </button>
                            )}
                            {step < 5 && (
                                <button type="button" className="btn btn-primary" onClick={handleNextStep}>
                                    Next
                                </button>
                            )}
                            {step === 5 && (
                                <button type="submit" className="btn btn-success">
                                    Submit
                                </button>
                            )}
                        </div>
                    </form>
                </div>
            </div>
        </div>
    );
};

export default ProjectForm;
