import { useState, useEffect } from "react";
import { Link } from "react-router-dom";


export default function ProjectList() {
    const [projects, setProjects] = useState([]);

    useEffect(() => {
        getProjects();
    }, []);

    function getProjects() {
        fetch("http://localhost:4000/api/Project/allProjects")
            .then(response => {
                if (response.ok) {
                    return response.json()
                }
                throw new Error()
            })
            .then(data => {
                console.log(data)
                setProjects(data)
            })
            .catch(error => {
                alert("Unable to get the data")
            })
    }

    return (
        <div className="container my-4">
            <h2 className="text-center mb-4">
                Projects
            </h2>

            <div className="row mb-3">
                <div className="col">
                    <Link className="btn btn-primary me-1" to="projects/create" role="button"> Create Project </Link>
                    <button type="button" className="btn btn-outline-primary">Refresh</button>
                </div>

                <div className="col"></div>
            </div>

            <table className="table">
                <thread>
                    <tr>
                        <th>Project Name</th>
                        <th>Name of order Company</th>
                        <th>Executor Company</th>
                        <th>Prirority</th>
                        <th>Leader</th>
                        <th>Start Date</th>
                        <th>End Date</th>
                    </tr>
                </thread>
                <tbody>
                    {
                        projects.map((projects, index) => {
                            return (
                                <tr key={index}>
                                    <td>{projects.Name}</td>
                                    <td>{projects.CustomerCompany}</td>
                                    <td>{projects.ExecutorCompany}</td>
                                    <td>{projects.Priority}</td>
                                    <td></td>
                                    <td></td>
                                    <td></td>
                                    <td style={{ width: "10px", whiteSpace: "nowrap" }}>
                                        <a className="btn btn-primary me-1" to="projects/create" role="button"> Edit </a>
                                        <button type="button" className="btn btn-outline-primary">Delete</button>

                                    </td>
                                </tr>
                            )
                        })
                    }
                </tbody>
            </table>
        </div>
    )
}
