import React, { useState, useEffect } from 'react';
import ProjectDetails from './ProjectDetails';
import EmployeeSelector from './EmployeeSelector';
import FileUploader from './FileUploader';
// import axios from 'axios';

const ProjectForm = () => {
    const [projects, setProjects] = useState([]);
    const [employees, setEmployees] = useState([]);
    const [selectedProject, setSelectedProject] = useState({});
    const [selectedEmployees, setSelectedEmployees] = useState([]);
    const [file, setFile] = useState(null);

    useEffect(() => {
        // fetchData();
    }, []);

    // const fetchData = async () => {
    //     try {
    //         const projectsResponse = await axios.get('/api/projects');
    //         setProjects(projectsResponse.data);

    //         const employeesResponse = await axios.get('/api/employees');
    //         setEmployees(employeesResponse.data);
    //     } catch (error) {
    //         console.error('Error fetching data:', error);
    //     }
    // };

    const handleProjectChange = (project) => {
        setSelectedProject(project);
    };

    const handleEmployeeChange = (employee) => {
        setSelectedEmployees([...selectedEmployees, employee]);
    };

    const handleFileChange = (file) => {
        setFile(file);
    };

    const handleFormSubmit = async (e) => {
        e.preventDefault();

        const formData = new FormData();
        formData.append('projectName', selectedProject.name);
        // Add other project details to formData

        // try {
        //     await axios.post('/api/projects/create', formData, {
        //         headers: { 'Content-Type': 'multipart/form-data' },
        //     });
        //     alert('Project created successfully!');
        //     fetchData(); // Refresh data after creating project
        // } catch (error) {
        //     console.error('Error creating project:', error);
        // }
    };

    return (
        <div>
            <h1>Create Project</h1>
            <form onSubmit={handleFormSubmit}>
                <ProjectDetails projects={projects} onChange={handleProjectChange} />
                <EmployeeSelector employees={employees} onChange={handleEmployeeChange} selectedEmployees={selectedEmployees} />
                <FileUploader onChange={handleFileChange} />
                <button type="submit">Create Project</button>
            </form>
        </div>
    );
};

export default ProjectForm;
