import React from 'react';

const ProjectDetails = ({ projects, onChange }) => {
    return (
        <div>
            <label>Project Name:</label>
            <select onChange={(e) => onChange(projects.find((p) => p.id === e.target.value))}>
                {projects.map((project) => (
                    <option key={project.id} value={project.id}>
                        {project.name}
                    </option>
                ))}
            </select>
            {/* Add other project fields (startDate, endDate, priority, etc.) here */}
        </div>
    );
};

export default ProjectDetails;
